/** * The Interface IQueue. * * @param <E> the element type * */public interface IQueue<E> {	/**	 * Adds the new entry.	 * 	 * @param newEntry the new entry	 */	public void add (E newEntry);	/**	 * Removes the entry.	 *	 * @return the entry removed	 */	public E remove ();	/**	 * Gets the entry.	 *	 * @return the entry	 */	public E get ();	/**	 *  Checks if is empty.	 * 	 * @return true, if is empty	 *	 */	public boolean isEmpty ();	/**	 * Clears the queue.	 */	public void clear ();} // interface