/** *The Interface IStack. * * @param <E> the element type */public interface IStack<E> {	/**	 * Pushes the new entry.	 *	 * @param entry the entry	 */	public void push (E entry);	/**	 * Pops the entry.	 *	 * @return the entry	 */	public E pop ();	/**	 * Peeks the entry (not remove).	 * 	 * @return the entry	 */	public E peek ();	/**	 * Checks if is empty.	 *	 * @return true, if is empty	 *	 */	public boolean isEmpty ();	/**	 * Clears the stack.	 */	public void clear (); 	/**	 * Gets the size.	 *	 * @return the size	 */	public int getSize ();} // interface